from datetime import datetime

import aiohttp


async def xm_sports():
    """
    小米运动主函数。

    从环境变量获取小米账号、密码和步数，并执行登录和修改步数的操作。
    """

    # 从环境变量获取小米账号、密码和步数
    user = os.environ.get('XmPhone')
    password = os.environ.get('XmPassword')
    step = os.environ.get('XmStep')

    # 检查账号和密码是否为空
    if user == '' or password == '':
        print("用户名或密码不能为空！")
        return

    # 登录获取应用令牌和用户ID
    app_token, user_id = await login_with_huami_account(user, password)

    # 根据应用令牌进行步数修改操作
    if app_token:
        await change_step_count(user, app_token, user_id, step)
    else:
        print("登录失败！")


def t():
    return time.strftime("%H:%M:%S", time.localtime())


